import { useEffect, useState } from "react";
import axios from "axios";

export default function AdminPanel() {
  const [productos, setProductos] = useState([]);
  const [pedidos, setPedidos] = useState([]);
  const [form, setForm] = useState({
    nombre: "",
    descripcion: "",
    precio: "",
    categoria: "",
    imagen: null
  });

  const API_URL = "http://localhost:8000/api";

  const token = localStorage.getItem("token");

  // --------------------- PRODUCTOS ---------------------
  const cargarProductos = async () => {
    try {
      const res = await axios.get(`${API_URL}/products`);
      setProductos(res.data);
    } catch (error) {
      console.error("Error cargando productos:", error);
      alert("Error cargando productos");
    }
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleImagen = (e) => {
    setForm({ ...form, imagen: e.target.files[0] });
  };

  const agregarProducto = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("nombre", form.nombre);
    formData.append("descripcion", form.descripcion);
    formData.append("precio", form.precio);
    formData.append("categoria", form.categoria);
    formData.append("imagen", form.imagen);

    try {
      await axios.post(`${API_URL}/products`, formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "multipart/form-data"
        }
      });
      cargarProductos();
      setForm({ nombre: "", descripcion: "", precio: "", categoria: "", imagen: null });
    } catch (error) {
      console.error("Error creando producto:", error);
      alert("Error creando producto");
    }
  };

  const eliminarProducto = async (id) => {
    if (!window.confirm("¿Eliminar producto?")) return;
    try {
      await axios.delete(`${API_URL}/products/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      cargarProductos();
    } catch (error) {
      console.error("Error eliminando producto:", error);
      alert("No se pudo eliminar el producto");
    }
  };

  // --------------------- PEDIDOS ---------------------
  const cargarPedidos = async () => {
    try {
      const res = await axios.get(`${API_URL}/sales`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setPedidos(res.data);
    } catch (error) {
      console.error("Error cargando pedidos:", error);
      alert("Error cargando pedidos");
    }
  };

  const cambiarEstado = async (id_sale, nuevoEstado) => {
    try {
      await axios.patch(`${API_URL}/sales/${id_sale}`, { estado: nuevoEstado }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      cargarPedidos();
    } catch (error) {
      console.error("Error cambiando estado:", error);
      alert("No se pudo actualizar el estado del pedido");
    }
  };

  useEffect(() => {
    cargarProductos();
    cargarPedidos();
  }, []);

  return (
    <div className="container mt-4">

      <h2>Panel de Administración</h2>

      {/* --------------------- FORMULARIO PRODUCTOS --------------------- */}
      <h3 className="mt-4">Agregar Producto</h3>
      <form onSubmit={agregarProducto} className="mb-4">
        <input className="form-control mb-2" name="nombre" placeholder="Nombre" value={form.nombre} onChange={handleChange} required />
        <textarea className="form-control mb-2" name="descripcion" placeholder="Descripción" value={form.descripcion} onChange={handleChange} required />
        <input className="form-control mb-2" type="number" name="precio" placeholder="Precio" value={form.precio} onChange={handleChange} required />
        <input className="form-control mb-2" name="categoria" placeholder="Categoría" value={form.categoria} onChange={handleChange} required />
        <input className="form-control mb-2" type="file" name="imagen" accept="image/*" onChange={handleImagen} required />
        {form.imagen && <img src={URL.createObjectURL(form.imagen)} alt="preview" style={{ width: "150px", marginBottom: "10px" }} />}
        <button className="btn btn-success w-100">Agregar Producto</button>
      </form>

      {/* --------------------- LISTA PRODUCTOS --------------------- */}
      <h3>Productos</h3>
      <table className="table table-bordered">
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Precio</th>
            <th>Imagen</th>
            <th>Acción</th>
          </tr>
        </thead>
        <tbody>
          {productos.map(p => (
            <tr key={p.id}>
              <td>{p.nombre}</td>
              <td>${p.precio}</td>
              <td><img src={`http://localhost:8000${p.imagen}`} alt={p.nombre} width="80" /></td>
              <td><button className="btn btn-danger btn-sm" onClick={() => eliminarProducto(p.id)}>Eliminar</button></td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* --------------------- LISTA PEDIDOS --------------------- */}
      <h3 className="mt-5">Pedidos</h3>
      <table className="table table-bordered">
        <thead>
          <tr>
            <th>ID Venta</th>
            <th>Usuario</th>
            <th>Total</th>
            <th>Estado</th>
            <th>Productos</th>
            <th>Acción</th>
          </tr>
        </thead>
        <tbody>
          {pedidos.length === 0 ? (
            <tr><td colSpan="6" className="text-center">No hay pedidos</td></tr>
          ) : (
            pedidos.map(p => (
              <tr key={p.id_sale}>
                <td>{p.id_sale}</td>
                <td>{p.identificacion_usuario}</td>
                <td>${Number(p.total).toLocaleString("es-CO")}</td>
                <td>{p.estado}</td>
                <td>
                  {p.SaleDetails?.map(d => (
                    <div key={d.id_detail}>{d.nombre_producto} x {d.cantidad}</div>
                  ))}
                </td>
                <td>
                  {p.estado === "pendiente" ? (
                    <button className="btn btn-success btn-sm" onClick={() => cambiarEstado(p.id_sale, "completada")}>
                      Marcar como completada
                    </button>
                  ) : (
                    <button className="btn btn-warning btn-sm" onClick={() => cambiarEstado(p.id_sale, "pendiente")}>
                      Marcar como pendiente
                    </button>
                  )}
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>

    </div>
  );
}
