      <input
        className="form-control mb-2"
        name="tipo_mueble"
        placeholder="Tipo de mueble (sofÃ¡, mesa, etc)"
        onChange={handleChange}
      />

      <input
        className="form-control mb-2"
        name="medidas"
        placeholder="Medidas (ej: 2m x 1m)"
        onChange={handleChange}
      />

      <input
        className="form-control mb-2"
        name="estilo"
        placeholder="Estilo (moderno, clÃ¡sico...)"
        onChange={handleChange}
      />

      <input
        className="form-control mb-2"
        name="material"
        placeholder="Material (madera, metal...)"
        onChange={handleChange}
      />

      <textarea
        className="form-control mb-3"
        name="descripcion"
        placeholder="Describe tu idea"
        onChange={handleChange}
      />

      <button className="btn btn-success" onClick={handleSubmit}>
        Enviar solicitud
