import { useState } from "react";
import axios from "axios";

export default function ChatIA() {
  const [mensajes, setMensajes] = useState([]);
  const [input, setInput] = useState("");

  const enviarMensaje = async () => {
    if (!input.trim()) return;

    // Mensaje del usuario
    const nuevoMensaje = {
      rol: "usuario",
      texto: input
    };

    setMensajes([...mensajes, nuevoMensaje]);
    setInput("");

    try {
      const res = await axios.post("http://localhost:8000/api/chat", {
        mensaje: input,
        emailCliente: "cliente@gmail.com"
      });

      // Respuesta de la IA
      setMensajes(prev => [
        ...prev,
        {
          rol: "ia",
          texto: JSON.stringify(res.data.respuestaIA)
        }
      ]);

    } catch (error) {
      setMensajes(prev => [
        ...prev,
        {
          rol: "ia",
          texto: "‚ùå Error grave al realizar la compra"
        }
      ]);
    }
  };

  return (
    <div style={styles.container}>
      <h2>ü§ñ Asistente Cozy Home</h2>

      <div style={styles.chat}>
        {mensajes.map((m, i) => (
          <div
            key={i}
            style={{
              ...styles.mensaje,
              alignSelf: m.rol === "usuario" ? "flex-end" : "flex-start",
              background: m.rol === "usuario" ? "#DCF8C6" : "#eee"
            }}
          >
            {m.texto}
          </div>
        ))}
      </div>

      <div style={styles.inputBox}>
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Escribe tu mensaje..."
          style={styles.input}
        />
        <button onClick={enviarMensaje} style={styles.btn}>Enviar</button>
      </div>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: "500px",
    margin: "30px auto",
    fontFamily: "Arial"
  },
  chat: {
    height: "400px",
    overflowY: "auto",
    border: "1px solid #ccc",
    padding: "10px",
    display: "flex",
    flexDirection: "column",
    gap: "10px"
  },
  mensaje: {
    padding: "10px",
    borderRadius: "8px",
    maxWidth: "80%"
  },
  inputBox: {
    display: "flex",
    marginTop: "10px"
  },
  input: {
    flex: 1,
    padding: "10px"
  },
  btn: {
    padding: "10px",
    background: "#000",
    color: "#fff",
    border: "none"
  }
};
