import { useEffect } from 'react';

export default function Inspiracion() {
  useEffect(() => {
    // üîπ CSS del chat n8n
    const link = document.createElement('link');
    link.rel = 'stylesheet';
    link.href = 'https://cdn.jsdelivr.net/npm/@n8n/chat/dist/style.css';
    document.head.appendChild(link);

    // üîπ CSS personalizado (bonito ‚ú®)
    const style = document.createElement('style');
    style.innerHTML = `
      /* Contenedor general de la p√°gina */
      .chat-page {
        min-height: 100vh;
        background: linear-gradient(135deg, #f8fafc, #eef2ff);
        display: flex;
        justify-content: center;
        align-items: center;
        padding: 40px 20px;
        font-family: 'Inter', system-ui, sans-serif;
      }

      /* Caja principal */
      .chat-wrapper {
        width: 100%;
        max-width: 900px;
        height: 80vh;
        background: #ffffff;
        border-radius: 20px;
        box-shadow: 0 20px 50px rgba(0,0,0,0.12);
        display: flex;
        flex-direction: column;
        overflow: hidden;
      }

      /* Header */
      .chat-header {
        padding: 20px;
        background: linear-gradient(135deg, #6366f1, #4f46e5);
        color: white;
        font-size: 1.4rem;
        font-weight: 600;
        text-align: center;
      }

      .chat-subtitle {
        font-size: 0.9rem;
        opacity: 0.85;
        margin-top: 4px;
      }

      /* √Årea del chat */
      #n8n-chat {
        flex: 1;
        padding: 10px;
      }

      /* Ajustes del chat n8n */
      #n8n-chat .n8n-chat {
        position: relative !important;
        inset: unset !important;
        height: 100% !important;
        max-height: 100% !important;
        border-radius: 0 !important;
        box-shadow: none !important;
      }

      /* Input m√°s lindo */
      #n8n-chat textarea {
        border-radius: 12px !important;
        border: 1px solid #d1d5db !important;
      }

      #n8n-chat button {
        border-radius: 12px !important;
        background: #4f46e5 !important;
      }

      #n8n-chat button:hover {
        background: #4338ca !important;
      }
    `;
    document.head.appendChild(style);

    // üîπ Script del chat
    const script = document.createElement('script');
    script.type = 'module';
    script.textContent = `
      import { createChat } from 'https://cdn.jsdelivr.net/npm/@n8n/chat/dist/chat.bundle.es.js';

      createChat({
        webhookUrl: 'http://localhost:5678/webhook/ec439a84-d76f-453e-802e-d7066889007f/chat',
        loadPreviousSession: false,
        enableStreaming: false,
        initialMessages: [
          'Hola üëã',
          'Soy el asistente de CozyHome üè°',
          '¬øEn qu√© puedo ayudarte hoy?'
        ],
      });
    `;
    document.body.appendChild(script);

    return () => {
      document.head.removeChild(link);
      document.head.removeChild(style);
      document.body.removeChild(script);
    };
  }, []);

  return (
    <div className="chat-page">
      <div className="chat-wrapper">
        <div className="chat-header">
          Asistente Inteligente CozyHome
          <div className="chat-subtitle">
            Atenci√≥n autom√°tica ‚Ä¢ Respuestas en tiempo real
          </div>
        </div>

        <div id="n8n-chat"></div>
      </div>
    </div>
  );
}
