import { useEffect } from 'react';

export default function Inspiracion() {
  useEffect(() => {
    /* ðŸ”¹ CSS oficial */
    const link = document.createElement('link');
    link.rel = 'stylesheet';
    link.href = 'https://cdn.jsdelivr.net/npm/@n8n/chat/dist/style.css';
    document.head.appendChild(link);

    /* ðŸ”¹ CSS personalizado: chat SIEMPRE visible */
    const style = document.createElement('style');
    style.innerHTML = `
      .chat-page {
        min-height: 100vh;
        background: linear-gradient(135deg, #f8fafc, #eef2ff);
        display: flex;
        justify-content: center;
        align-items: center;
        padding: 40px 20px;
        font-family: 'Inter', system-ui, sans-serif;
      }

      .chat-wrapper {
        width: 100%;
        max-width: 900px;
        height: 80vh;
        background: #ffffff;
        border-radius: 20px;
        box-shadow: 0 20px 50px rgba(0,0,0,0.12);
        overflow: hidden;
        display: flex;
        flex-direction: column;
      }

      .chat-header {
        padding: 18px;
        background: linear-gradient(135deg, #6366f1, #4f46e5);
        color: white;
        font-size: 1.3rem;
        font-weight: 600;
        text-align: center;
      }

      #n8n-chat {
        flex: 1;
      }

      /* ðŸ”¥ FORZAR CHAT ABIERTO */
      .n8n-chat-widget {
        position: static !important;
        inset: unset !important;
        height: 100% !important;
        width: 100% !important;
        max-height: 100% !important;
        border-radius: 0 !important;
        box-shadow: none !important;
        display: flex !important;
      }

      /* âŒ OCULTAR BOTÃ“N FLOTANTE */
      .n8n-chat-launcher,
      .n8n-chat-toggle,
      .n8n-chat-button {
        display: none !important;
      }

      /* Ajustes internos */
      .n8n-chat-messages {
        padding: 16px !important;
      }

      .n8n-chat-input {
        border-top: 1px solid #e5e7eb !important;
      }

      textarea {
        border-radius: 12px !important;
      }

      button {
        border-radius: 12px !important;
        background: #4f46e5 !important;
      }
    `;
    document.head.appendChild(style);

    /* ðŸ”¹ Script */
    const script = document.createElement('script');
    script.type = 'module';
    script.textContent = `
      import { createChat } from 'https://cdn.jsdelivr.net/npm/@n8n/chat/dist/chat.bundle.es.js';

      createChat({
        webhookUrl: 'http://localhost:5678/webhook/ec439a84-d76f-453e-802e-d7066889007f/chat',
        loadPreviousSession: false,
        enableStreaming: false,
        initialMessages: [
          'Hola ðŸ‘‹',
          'Soy el asistente de CozyHome ðŸ¡',
          'EscrÃ­beme directamente aquÃ­ ðŸ‘‡'
        ],
      });
    `;
    document.body.appendChild(script);

    return () => {
      document.head.removeChild(link);
      document.head.removeChild(style);
      document.body.removeChild(script);
    };
  }, []);

  return (
    <div className="chat-page">
      <div className="chat-wrapper">
        <div className="chat-header">
          Asistente Inteligente CozyHome
        </div>

        <div id="n8n-chat"></div>
      </div>
    </div>
  );
}
