import { useEffect, useState } from "react";
import axios from "axios";

export default function Pedidos() {
  const user = JSON.parse(localStorage.getItem("user"));
  const [sales, setSales] = useState([]);
  const [error, setError] = useState(false);

  useEffect(() => {
    if (!user) return;

    axios
      .get(`http://localhost:8000/api/sales/${user.identificacion}`)
      .then(res => {
        setSales(res.data);
      })
      .catch(err => {
        console.error(err);
        setError(true);
      });
  }, []);

  if (error) return <p>Error cargando pedidos</p>;

  return (
    <div className="container mt-4">
      <h2>Mis pedidos</h2>

      {sales.length === 0 && (
        <p>No has realizado compras a√∫n</p>
      )}

      {sales.map(sale => (
        <div key={sale.id_sale} className="card mb-4">
          <div className="card-header">
            Compra #{sale.id_sale}
          </div>

          <div className="card-body">
            <p>
              <strong>Fecha:</strong>{" "}
              {new Date(sale.fecha).toLocaleDateString()}
            </p>

            <p>
              <strong>Total:</strong>{" "}
              ${sale.total.toLocaleString("es-CO")}
            </p>

            <h6>Productos comprados</h6>

            <ul className="list-group">
              {sale.detalles.map(detalle => (
                <li key={detalle.id_detail} className="list-group-item">
                  <strong>{detalle.nombre_producto}</strong>
                  <div>Precio: ${detalle.precio.toLocaleString("es-CO")}</div>
                  <div>Cantidad: {detalle.cantidad}</div>
                </li>
              ))}
            </ul>
          </div>
        </div>
      ))}
    </div>
  );
}
