import { useEffect, useState } from "react";
import axios from "axios";

export default function Pedidos() {
  const user = JSON.parse(localStorage.getItem("user"));
  const token = localStorage.getItem("token");

  const [sales, setSales] = useState([]);

  useEffect(() => {
    axios.get(
      `http://localhost:8000/api/sales/${user.identificacion}`,
      {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      }
    ).then(res => {
      setSales(res.data);
    }).catch(err => {
      console.error(err);
    });
  }, []);

  return (
    <div className="container mt-4">
      <h2>ðŸ§¾ Mis pedidos</h2>

      {sales.length === 0 && (
        <p>No has realizado compras aÃºn</p>
      )}

      {sales.map(sale => (
        <div key={sale.id_sale} className="card mb-4">
          <div className="card-header">
            <strong>Compra #{sale.id_sale}</strong>
          </div>

          <div className="card-body">
            <p>
              <strong>Fecha:</strong>{" "}
              {new Date(sale.fecha).toLocaleDateString()}
            </p>

            <p>
              <strong>Total:</strong>{" "}
              ${sale.total.toLocaleString("es-CO")}
            </p>

            <h6>Productos comprados</h6>

            <ul className="list-group">
              {sale.SaleDetails.map(detail => (
                <li key={detail.id_detail} className="list-group-item">
                  <div><strong>ID Producto:</strong> {detail.id_producto}</div>
                  <div><strong>{detail.nombre_producto}</strong></div>
                  <div>
                    Precio: ${detail.precio.toLocaleString("es-CO")}
                  </div>
                  <div>Cantidad: {detail.cantidad}</div>
                </li>
              ))}
            </ul>
          </div>
        </div>
      ))}
    </div>
  );
}
