import { useEffect } from 'react';

export default function Inspiracion() {
  useEffect(() => {
    //  Inyectar CSS (todo aqu铆 mismo)
    const style = document.createElement('style');
    style.innerHTML = `
      /* Reset general */
      html, body {
        margin: 0;
        padding: 0;
        height: 100%;
        background: #f2f4f8;
        font-family: system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
      }

      /* Contenedor principal del chat */
      #n8n-chat {
        width: 100%;
        height: 100vh;
        display: flex;
      }

      /* ====== Personalizaci贸n n8n Chat ====== */
      :root {
        --chat--color--primary: #1f2937;
        --chat--color--secondary: #2563eb;
        --chat--color-light: #f9fafb;
        --chat--color-dark: #111827;

        --chat--window--width: 100%;
        --chat--window--height: 100%;
        --chat--window--border-radius: 0;
        --chat--window--border: none;

        --chat--header--background: #111827;
        --chat--header--color: #ffffff;

        --chat--message--bot--background: #ffffff;
        --chat--message--bot--color: #111827;

        --chat--message--user--background: #2563eb;
        --chat--message--user--color: #ffffff;

        --chat--input--background: #ffffff;
        --chat--input--border: 1px solid #e5e7eb;

        --chat--toggle--size: 0px; /* elimina bot贸n flotante */
      }
    `;
    document.head.appendChild(style);

    //  Cargar CSS oficial n8n
    const link = document.createElement('link');
    link.rel = 'stylesheet';
    link.href = 'https://cdn.jsdelivr.net/npm/@n8n/chat/dist/style.css';
    document.head.appendChild(link);

    //  Script del chat
    const script = document.createElement('script');
    script.type = 'module';
    script.textContent = `
      import { createChat } from 'https://cdn.jsdelivr.net/npm/@n8n/chat/dist/chat.bundle.es.js';

      createChat({
        webhookUrl: 'http://localhost:5678/webhook/ec439a84-d76f-453e-802e-d7066889007f/chat',
        mode: 'fullscreen',
        target: '#n8n-chat',
        loadPreviousSession: false,
        enableStreaming: false,
        initialMessages: [
          'Hola ',
          'Soy el asistente de CozyHome. 驴En qu茅 te ayudo hoy?'
        ],
        i18n: {
          en: {
            title: 'CozyHome IA ',
            subtitle: 'Asistente inteligente',
            inputPlaceholder: 'Escribe tu mensaje...'
          }
        }
      });
    `;
    document.body.appendChild(script);

    return () => {
      document.head.removeChild(style);
      document.head.removeChild(link);
      document.body.removeChild(script);
    };
  }, []);

  return <div id="n8n-chat"></div>;
}
