import { useEffect, useState } from "react";
import "./index.css";

export default function PaymentForm() {
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);

  useEffect(() => {
    const storedCart = localStorage.getItem("carrito");

    if (storedCart) {
      const parsedCart = JSON.parse(storedCart);
      setCart(parsedCart);

      const totalCalculado = parsedCart.reduce(
        (sum, p) => sum + Number(p.precio) * Number(p.cantidad || 1),
        0
      );

      setTotal(totalCalculado);
    }
  }, []);

  const handlePayment = () => {
    alert(`Pago realizado por $${total.toLocaleString("es-CO")} COP`);
    localStorage.removeItem("carrito");
  };

  return (
    <div className="checkout-container">

      {/* FORMULARIO */}
      <div className="checkout-form">
        <h2>Cozy Home</h2>
        <p className="subtitle">Sistema de pagos</p>

        <label>Correo electr√≥nico</label>
        <input type="email" />

        <label>Nombre completo</label>
        <input type="text" />

        <button onClick={handlePayment}>
          Pagar ${total.toLocaleString("es-CO")}
        </button>
      </div>

      {/* RESUMEN DE COMPRA */}
      <div className="checkout-summary">
        <h3>Resumen</h3>

        {cart.map((p, i) => (
          <div key={i} className="summary-item">
            <img
              src={`http://localhost:8000${p.imagen}`}
              alt={p.nombre}
              width="50"
            />
            <div>
              <strong>{p.nombre}</strong>
              <p>${p.precio}</p>
            </div>
          </div>
        ))}

        <hr />

        <div className="summary-total">
          <span>Total</span>
          <strong>${total.toLocaleString("es-CO")}</strong>
        </div>
      </div>

    </div>
  );
}
