import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./pago.css";

export default function PaymentForm() {
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);

  // Campos del formulario
  const [email, setEmail] = useState("");
  const [nombres, setNombres] = useState("");
  const [apellidos, setApellidos] = useState("");
  const [tipoDocumento, setTipoDocumento] = useState("Cédula");
  const [numeroDocumento, setNumeroDocumento] = useState("");
  const [celular, setCelular] = useState("");

  const navigate = useNavigate();

  // Cargar carrito al iniciar
  useEffect(() => {
    const storedCart = localStorage.getItem("carrito");

    if (storedCart) {
      const parsedCart = JSON.parse(storedCart);
      setCart(parsedCart);

      const totalCalculado = parsedCart.reduce(
        (sum, p) => sum + Number(p.precio) * Number(p.cantidad || 1),
        0
      );

      setTotal(totalCalculado);
    }
  }, []);

  // Función para eliminar un producto del carrito
  const removeItem = (index) => {
    const newCart = cart.filter((_, i) => i !== index);
    setCart(newCart);
    localStorage.setItem("carrito", JSON.stringify(newCart));
  };

  // Función para procesar el pago
  const handlePayment = async () => {
    if (!email || !nombres || !apellidos || !numeroDocumento || !celular) {
      alert("Por favor, completa todos los campos del formulario.");
      return;
    }

    if (cart.length === 0) {
      alert("El carrito está vacío.");
      return;
    }

    try {
      // Enviar datos al backend
      const response = await fetch("http://localhost:8000/api/sales", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          total: total,
          identificacion_usuario: numeroDocumento, // ID del usuario
          cart: cart, // productos completos
          email,
          nombres,
          apellidos,
          tipoDocumento,
          celular
        }),
      });

      const data = await response.json();

      if (response.ok) {
        alert(
          `Pago realizado por $${total.toLocaleString("es-CO")} COP\n` +
          `Gracias por comprar en Cozy Home, ${nombres} ${apellidos}.\n` +
          `Documento: ${tipoDocumento} ${numeroDocumento}\n` +
          `Celular: ${celular}\n` +
          `Venta registrada con ID: ${data.venta.id_sale}`
        );

        // Limpiar carrito
        localStorage.removeItem("carrito");
        setCart([]);
        setTotal(0);

        // Volver a productos o inicio
        navigate("/productos");
      } else {
        alert("Error al registrar la venta: " + data.message);
      }
    } catch (error) {
      console.error("Error al procesar el pago:", error);
      alert("Ocurrió un error al procesar el pago. Revisa la consola.");
    }
  };

  return (
    <div className="checkout-container">

      {/* FORMULARIO */}
      <div className="checkout-form">
        <h2>Cozy Home</h2>
        <p className="subtitle">Sistema de pagos</p>

        <label>Correo electrónico</label>
        <input
          type="email"
          placeholder="correo@ejemplo.com"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />

        <div className="row">
          <div>
            <label>Nombres</label>
            <input
              type="text"
              value={nombres}
              onChange={(e) => setNombres(e.target.value)}
            />
          </div>
          <div>
            <label>Apellidos</label>
            <input
              type="text"
              value={apellidos}
              onChange={(e) => setApellidos(e.target.value)}
            />
          </div>
        </div>

        <div className="row">
          <div>
            <label>Tipo de documento</label>
            <select
              value={tipoDocumento}
              onChange={(e) => setTipoDocumento(e.target.value)}
            >
              <option>Cédula</option>
              <option>Pasaporte</option>
            </select>
          </div>
          <div>
            <label>Número</label>
            <input
              type="text"
              value={numeroDocumento}
              onChange={(e) => setNumeroDocumento(e.target.value)}
            />
          </div>
        </div>

        <label>Celular</label>
        <input
          type="text"
          value={celular}
          onChange={(e) => setCelular(e.target.value)}
        />

        <button className="btn btn-success" onClick={handlePayment}>
          Pagar ${total.toLocaleString("es-CO")}
        </button>
      </div>

      {/* RESUMEN DE COMPRA */}
      <div className="checkout-summary">
        <h3>Resumen</h3>

        {cart.length === 0 ? (
          <p>Tu carrito está vacío</p>
        ) : (
          cart.map((p, i) => (
            <div key={i} className="summary-item">
              <div>
                <strong>{p.nombre}</strong>
                <p>${Number(p.precio).toLocaleString("es-CO")}</p>
                <p>Cantidad: {p.cantidad || 1}</p>
                <button
                  className="btn btn-danger btn-sm"
                  onClick={() => removeItem(i)}
                >
                  Eliminar
                </button>
              </div>
            </div>
          ))
        )}

        <hr />

        <div className="summary-total">
          <span>Total</span>
          <strong>${total.toLocaleString("es-CO")}</strong>
        </div>
      </div>

    </div>
  );
}
