import { useEffect } from "react";

export default function Inspiracion() {
  useEffect(() => {
    /* CSS n8n */
    const link = document.createElement("link");
    link.rel = "stylesheet";
    link.href = "https://cdn.jsdelivr.net/npm/@n8n/chat/dist/style.css";
    document.head.appendChild(link);

    /* CSS personalizado */
    const style = document.createElement("style");
    style.innerHTML = `
      body {
        margin: 0;
      }

      .chat-page {
        min-height: 100vh;
        background: linear-gradient(135deg, #f8fafc, #eef2ff);
        display: flex;
        justify-content: center;
        align-items: center;
        padding: 40px;
        font-family: system-ui, sans-serif;
      }

      .chat-wrapper {
        width: 100%;
        max-width: 900px;
        height: 80vh;
        background: white;
        border-radius: 20px;
        box-shadow: 0 20px 50px rgba(0,0,0,0.12);
        overflow: hidden;
        display: flex;
        flex-direction: column;
      }

      .chat-header {
        padding: 16px;
        background: linear-gradient(135deg, #6366f1, #4f46e5);
        color: white;
        font-size: 1.3rem;
        font-weight: 600;
        text-align: center;
      }

      /* üî• FORZAR CHAT SIEMPRE ABIERTO */
      .n8n-chat-widget {
        position: static !important;
        width: 100% !important;
        height: 100% !important;
        max-height: none !important;
        border-radius: 0 !important;
        box-shadow: none !important;
        display: flex !important;
      }

      /* ‚ùå quitar bot√≥n flotante */
      .n8n-chat-launcher,
      .n8n-chat-toggle,
      .n8n-chat-button {
        display: none !important;
      }
    `;
    document.head.appendChild(style);

    /* Script */
    const script = document.createElement("script");
    script.type = "module";
    script.innerHTML = `
      import { createChat } from "https://cdn.jsdelivr.net/npm/@n8n/chat/dist/chat.bundle.es.js";

      createChat({
        webhookUrl: "http://localhost:5678/webhook/ec439a84-d76f-453e-802e-d7066889007f/chat",
        loadPreviousSession: false,
        initialMessages: [
          "Hola üëã",
          "Soy el asistente de CozyHome üè°",
          "Escr√≠beme directamente aqu√≠"
        ],
      });
    `;
    document.body.appendChild(script);

    return () => {
      document.head.removeChild(link);
      document.head.removeChild(style);
      document.body.removeChild(script);
    };
  }, []);

  return (
    <div className="chat-page">
      <div className="chat-wrapper">
        <div className="chat-header">
          Asistente Inteligente CozyHome
        </div>
        {/* n8n se monta solo, aqu√≠ no va nada */}
      </div>
    </div>
  );
}
