export default function Inspiracion() {
  useEffect(() => {
    // ðŸ”¹ CSS inline (todo aquÃ­)
    const style = document.createElement('style');
    style.innerHTML = `
      html, body {
        margin: 0;
        padding: 0;
        background: #f2f4f8;
      }

      /* Contenedor general de la pÃ¡gina */
      .chat-page-wrapper {
        display: flex;
        justify-content: center;
        align-items: flex-start;
        padding: 2rem 0;
      }

      /* Caja del chat (60%) */
      #n8n-chat {
        width: 60%;
        height: 75vh;
        background: #ffffff;
        border-radius: 12px;
        overflow: hidden;
        box-shadow: 0 10px 30px rgba(0,0,0,0.12);
      }

      /* ===== Variables n8n ===== */
      :root {
        --chat--window--width: 100%;
        --chat--window--height: 100%;
        --chat--window--border-radius: 12px;
        --chat--window--border: none;

        --chat--header--background: linear-gradient(135deg, #1e1b4b, #312e81);
        --chat--header--color: #ffffff;

        --chat--message--user--background: #2563eb;
        --chat--message--user--color: #ffffff;

        --chat--message--bot--background: #f9fafb;
        --chat--message--bot--color: #111827;

        --chat--toggle--size: 0px; /* elimina botÃ³n flotante */
      }

      /* Responsive */
      @media (max-width: 1024px) {
        #n8n-chat {
          width: 90%;
          height: 80vh;
        }
      }
    `;
    document.head.appendChild(style);

    // ðŸ”¹ CSS oficial n8n
    const link = document.createElement('link');
    link.rel = 'stylesheet';
    link.href = 'https://cdn.jsdelivr.net/npm/@n8n/chat/dist/style.css';
    document.head.appendChild(link);

    // ðŸ”¹ Script n8n chat
    const script = document.createElement('script');
    script.type = 'module';
    script.textContent = `
      import { createChat } from 'https://cdn.jsdelivr.net/npm/@n8n/chat/dist/chat.bundle.es.js';

      createChat({
        webhookUrl: 'http://localhost:5678/webhook/ec439a84-d76f-453e-802e-d7066889007f/chat',
        target: '#n8n-chat',
        mode: 'window', // ðŸ‘ˆ CLAVE: NO fullscreen
        loadPreviousSession: false,
        enableStreaming: false,
        initialMessages: [
          'Hola ðŸ‘‹',
          'Soy el asistente de CozyHome. Â¿En quÃ© te ayudo hoy?'
        ],
        i18n: {
          en: {
            title: 'CozyHome IA ðŸ¤–',
            subtitle: 'Asistente inteligente',
            inputPlaceholder: 'Escribe tu mensaje...'
          }
        }
      });
    `;
    document.body.appendChild(script);

    return () => {
      document.head.removeChild(style);
      document.head.removeChild(link);
      document.body.removeChild(script);
    };
  }, []);

  return (
    <div className="chat-page-wrapper">
      <div id="n8n-chat"></div>
    </div>
  );
}
